INSERT INTO `jieqi_system_configs` (`cid`, `modname`, `cname`, `ctitle`, `cvalue`, `cdescription`, `cdefine`, `ctype`, `options`, `catorder`, `catname`) VALUES(0, 'article', 'fakeinfo', '文章信息页面伪静态规则', '', '\r\n伪静态规则是带有替换标记的路径，留空表示不使用伪静态。\r\n允许使用的替换标记有 <{$id}> 文章ID ,<{$id|subdirectory}> 根据文章ID生成的子目录。\r\n如：/files/article/info<{$id|subdirectory}>/<{$id}>.htm', 0, 1, '', 21910, '文件参数');
INSERT INTO `jieqi_system_configs` (`cid`, `modname`, `cname`, `ctitle`, `cvalue`, `cdescription`, `cdefine`, `ctype`, `options`, `catorder`, `catname`) VALUES(0, 'article', 'fakesort', '文章分类页面伪静态规则', '', '\r\n伪静态规则是带有替换标记的路径，留空表示不使用伪静态。\r\n允许使用的替换标记有 <{$class}> 分类ID ,<{$page}> 页码，<{$page|subdirectory}> 根据页码生成的子目录。\r\n如：/files/article/sort<{$class}><{$page|subdirectory}>/<{$page}>.htm', 0, 1, '', 21920, '文件参数');
INSERT INTO `jieqi_system_configs` (`cid`, `modname`, `cname`, `ctitle`, `cvalue`, `cdescription`, `cdefine`, `ctype`, `options`, `catorder`, `catname`) VALUES(0, 'article', 'fakeinitial', '首字母分类页面伪静态规则', '', '\r\n伪静态规则是带有替换标记的路径，留空表示不使用伪静态。\r\n允许使用的替换标记有 <{$initial}> 首字母 ,<{$page}> 页码，<{$page|subdirectory}> 根据页码生成的子目录。\r\n如：/files/article/initial<{$initial}><{$page|subdirectory}>/<{$page}>.htm', 0, 1, '', 21930, '文件参数');
INSERT INTO `jieqi_system_configs` (`cid`, `modname`, `cname`, `ctitle`, `cvalue`, `cdescription`, `cdefine`, `ctype`, `options`, `catorder`, `catname`) VALUES(0, 'article', 'faketoplist', '排行榜页面伪静态规则', '', '\r\n伪静态规则是带有替换标记的路径，留空表示不使用伪静态。\r\n允许使用的替换标记有 <{$sort}> 排行列表 ,<{$page}> 页码，<{$page|subdirectory}> 根据页码生成的子目录。\r\n如：/files/article/top<{$sort}><{$page|subdirectory}>/<{$page}>.htm', 0, 1, '', 21940, '文件参数');
