INSERT INTO `jieqi_system_configs` (`cid`, `modname`, `cname`, `ctitle`, `cvalue`, `cdescription`, `cdefine`, `ctype`, `options`, `catorder`, `catname`) VALUES(0, 'system', 'JIEQI_CACHE_DIR', '网页缓存路径', 'cache', '支持三种写法：1、只填目录名（指网站跟目录下的子目录名），如： cahce ；2、使用完整路径，如： D:/web/cache ；3、使用Memcached，格式为 memcached://服务地址:端口号，如：memcached://127.0.0.1:11211', 1, 1, '', 32320, '显示控制');
